/* ===== Colors to match screenshot-2 ===== */
.tgv-top-section{
  --bg: #0a0f2b;           /* dark page bg */
  --panel: #0b1030;        /* right box bg */
  --accent: #EE6A4D;       /* coral border/accent */
  --heading: #ff5c5c;      /* coral heading */
  --text: #ffffff;

  background: var(--bg);
  color: var(--text);
  padding: 48px 20px 36px;
  font-family: 'Manrope', sans-serif;
}

/* header row */
.tgv-top-header{
  display:flex;
  justify-content: space-between;
  align-items:center;
  margin-bottom: 24px;
}
.tgv-top-heading{
  font-size: 32px;
  font-weight: 700;
  color: var(--heading);
}

/* make "View all" small white pill with coral border */
.tgv-top-section .tgv-view-btn{
  background: #fff;
  color: var(--accent);
  border: 1px solid var(--accent);
  margin-right: 450px;
  padding: 8px 16px;
  font-weight: 600;
  font-size: 16px;
  border-radius: 8px;
  cursor: pointer;
}
.tgv-top-section .tgv-view-btn:hover{
  background: var(--accent);
  color: #fff;
}

/* 2-column layout: left cards, right info box */
.tgv-top-content{
  display: grid;
  grid-template-columns: 1fr 420px;   /* LEFT flexible | RIGHT fixed */
  gap: 32px;
  align-items: start;
}

/* 3 cards in a row */
.tgv-cards-container{
  display: grid;
  grid-template-columns: repeat(3, minmax(240px, 1fr));
  gap: 24px;
}

/* card look = white tile with light shadow */
.tgv-card{
  background: #ffffff;
  border-radius: 14px;
  padding: 16px;
  box-shadow: 0 8px 20px rgba(0,0,0,.12);
}
.img1{
  width: 100%;
  height: auto;
  object-fit: contain;
}

/* right panel look */
.tgv-know-box{
  background: var(--panel);
  border: 1px solid var(--accent);
  border-radius: 12px;
  padding: 24px;
  color: #fff;
}

.tgv-know-title{
  font-size: 28px;
  font-weight: 700;
  margin-bottom: 10px;
}
.tgv-know-text{
  font-size: 16px;
  font-weight: 600;
  line-height: 1.6;
  margin-bottom: 24px;
  color: #e7e7e7;
}
.tgv-about-btn{
  background: var(--accent);
  color: #fff;
  border: none;
  padding: 10px 16px;
  border-radius: 8px;
  font-weight: 700;
  cursor: pointer;
}

/* ---------- responsive ---------- */
@media (max-width: 1200px){
  .tgv-top-content{ grid-template-columns: 1fr 360px; }
}
@media (max-width: 900px){
  .tgv-top-content{ grid-template-columns: 1fr; }  /* stack */
  .tgv-cards-container{ grid-template-columns: repeat(2, minmax(200px,1fr)); }
}
@media (max-width: 560px){
  .tgv-cards-container{ grid-template-columns: 1fr; }
}
















