/* Container */
.SET-wrap{
  max-width: 1080px;
  margin: 32px auto 64px;
  padding: 0 20px;
  font-family: "Segoe UI", sans-serif;
  color: #1c1c1c;
}

/* Title */
.SET-title{
  font-size: 40px;
  line-height: 1.2;
  font-weight: 700;
  margin-bottom: 16px;
  color: #0b0b0b;
}

/* Responsive YouTube */
.SET-video{
  position: relative;
  width: 100%;
  aspect-ratio: 16 / 9;
  background: #000;
  border-radius: 12px;
  overflow: hidden;
  margin: 14px 0 22px;
}
.SET-video iframe{
  width: 100%;
  height: 100%;
  border: 0;
  display: block;
}

/* Sections & text */
.SET-section{ margin-top: 22px; }
.SET-h2{
  font-size: 26px;
  font-weight: 700;
  margin: 10px 0 12px;
  color: #0b278f;
}
.SET-p{ font-size: 16px; line-height: 1.7; margin-bottom: 10px; }
.SET-list, .SET-olist{
  margin: 8px 0 14px 20px;
  font-size: 16px;
  line-height: 1.7;
}
.SET-tight li{ margin: 2px 0; }
.SET-link{ color:#0b278f; text-decoration: underline; }

/* Courses â€” allow wrapping cleanly */
.SET-cols{ display:block; }

/* Table (match screenshot: clean grid, light header) */
.SET-table-wrapper{
  width: 100%;
  overflow-x: auto;
  margin: 10px 0 12px;
  border-radius: 6px;
  background: #fff;
}
.SET-table{
  width: 100%;
  min-width: 520px;            /* keep structure on small viewports */
  border-collapse: collapse;
  table-layout: fixed;
  border: 1px solid #6e6e6e;   /* outer border like screenshot */
}
.SET-th{
  background: #f1f1f1;         /* light gray header */
  color: #111;
  font-weight: 700;
  border: 1px solid #6e6e6e;
  padding: 9px 8px;
  text-align: center;
}
.SET-td{
  border: 1px solid #6e6e6e;
  padding: 9px 8px;
  background: #fff;
  color: #1c1c1c;
}
.SET-center{ text-align: center; }
.SET-left{ text-align: left; }
.SET-bold{ font-weight: 700; }
.SET-total-row .SET-td{
  background: #fcfcfc;
  font-weight: 700;
}

/* Mobile */
@media (max-width: 520px){
  .SET-title{ font-size: 30px; }
  .SET-h2{ font-size: 22px; }
  .SET-p, .SET-list, .SET-olist{ font-size: 15px; }
}

