/* ===========================
   TEAM MOBILE SCROLL STRIPS
   (append at end of Team.css)
   =========================== */

/* Desktop/tablet: existing grid visible (we keep your forced 4-col grid).
   We just mark it as 'desktop' explicitly in JSX and let it show by default. */
.tt-team-grid-desktop {
  /* nothing extra for desktop; your existing .tt-team-grid rules already apply */
}

/* Hide mobile scroller by default (desktop/tablet) */
.tt-team-scroller {
  display: none;
}

/* ðŸ“± Mobile */
@media (max-width: 768px) {
  /* Hide the original grid on mobile and show the two scrollers */
  .tt-team-grid-desktop {
    display: none !important;
  }

  .tt-team-scroller {
    display: flex;
    flex-direction: column;
    gap: 14px;
    width: 100%;
  }

  /* Each strip is a horizontal scroll row */
  .tt-team-strip {
    display: flex;
    gap: 12px;
    overflow-x: auto;
    padding: 2px 2px 8px;
    -webkit-overflow-scrolling: touch;
    scroll-snap-type: x mandatory;
  }

  .tt-team-strip::-webkit-scrollbar {
    height: 6px;
  }
  .tt-team-strip::-webkit-scrollbar-thumb {
    background: #c7c7c7;
    border-radius: 3px;
  }
  .tt-team-strip::-webkit-scrollbar-track {
    background: transparent;
  }

  /* Cards inside strips: fixed width so they slide nicely */
  .tt-team-strip .tt-team-card {
    flex: 0 0 auto;
    width: 72%;
    max-width: 260px;
    min-width: 220px;
    scroll-snap-align: start;
    border-radius: 16px;
  }

  /* Make images look clean on mobile (no stretch) */
  .tt-team-strip .tt-team-card img {
    width: 100%;
    height: 160px;      /* fixed height for uniform tiles */
    object-fit: cover;  /* crop smartly; no distortion */
    border-radius: 16px 16px 0 0;
    display: block;
  }

  /* Name badge a bit tighter on small screens */
  .tt-team-strip .tt-team-name {
    font-size: 14px;
    margin-top: 10px;
    padding: 6px 10px;
  }
}

/* ====== TEAM DESKTOP GRID: EXACT 4 COLUMNS (4Ã—2) ====== */
.tt-team-grid.tt-team-grid-desktop {
  display: grid !important;
  grid-template-columns: repeat(4, minmax(0, 1fr)) !important;
  gap: 30px;
  align-items: stretch;
}

/* Desktop/Tablet me scroller hide, grid show */
@media (min-width: 769px) {
  .tt-team-scroller { display: none !important; }
  .tt-team-grid.tt-team-grid-desktop { display: grid !important; }
}

/* Mobile me grid hide, scroller show */
@media (max-width: 768px) {
  .tt-team-grid.tt-team-grid-desktop { display: none !important; }
  .tt-team-scroller { display: flex !important; }
}

/* ====== MOBILE STRIPS (TOP 4 + BOTTOM 4) ====== */
.tt-team-scroller {
  display: none;              /* default hidden; mobile me show */
  flex-direction: column;
  gap: 16px;
  margin-top: 8px;
}

.tt-team-strip {
  display: flex;
  gap: 16px;
  overflow-x: auto;
  padding: 4px 4px 10px;
  -webkit-overflow-scrolling: touch;
  scroll-snap-type: x mandatory;     /* smooth snapping */
}

.tt-team-strip .tt-team-card {
  flex: 0 0 72%;
  max-width: 72%;
  scroll-snap-align: start;
}

/* thoda bada devices pe width adjust */
@media (min-width: 420px) and (max-width: 768px) {
  .tt-team-strip .tt-team-card {
    flex-basis: 55%;
    max-width: 55%;
  }
}

/* Card/Img defaults â€” use existing style, just ensure responsive */
.tt-team-card img {
  width: 100%;
  height: auto;
  display: block;
}

/* Optional: mobile strip scrollbar styling */
.tt-team-strip::-webkit-scrollbar {
  height: 8px;
}
.tt-team-strip::-webkit-scrollbar-thumb {
  background: #cfcfcf;
  border-radius: 4px;
}
.tt-team-strip::-webkit-scrollbar-track {
  background: transparent;
}

