.course-viewer {
  min-height: 100vh;
  background: #f8fafc;
  display: flex;
  flex-direction: column;
}

.course-viewer.loading,
.course-viewer.error {
  justify-content: center;
  align-items: center;
}

.loading-spinner {
  text-align: center;
  padding: 2rem;
  font-size: 1.1rem;
  color: #64748b;
}

.error-message {
  text-align: center;
  padding: 2rem;
  background: white;
  border-radius: 12px;
  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
  max-width: 400px;
}

.error-message h2 {
  color: #dc2626;
  margin-bottom: 1rem;
}

.error-message p {
  color: #64748b;
  margin-bottom: 1.5rem;
}

/* Header */
.course-header {
  background: white;
  border-bottom: 1px solid #e2e8f0;
  padding: 1.5rem 2rem;
  box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1);
}

.course-title {
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-wrap: wrap;
  gap: 1rem;
}

.course-title h1 {
  margin: 0;
  color: #1f2937;
  font-size: 1.75rem;
  font-weight: 700;
}

.course-progress {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  min-width: 200px;
}

.progress-bar {
  flex: 1;
  height: 8px;
  background: #e5e7eb;
  border-radius: 4px;
  overflow: hidden;
}

.progress-fill {
  height: 100%;
  background: linear-gradient(90deg, #3b82f6, #1d4ed8);
  border-radius: 4px;
  transition: width 0.3s ease;
}

.progress-text {
  font-size: 0.875rem;
  color: #64748b;
  white-space: nowrap;
}

/* Layout */
.course-layout {
  display: flex;
  flex: 1;
  height: calc(100vh - 120px);
}

/* Sidebar */
.course-sidebar {
  width: 350px;
  background: white;
  border-right: 1px solid #e2e8f0;
  display: flex;
  flex-direction: column;
  overflow: hidden;
}

.sidebar-header {
