}

.lesson-icon {
  flex-shrink: 0;
  width: 24px;
  height: 24px;
  display: flex;
  align-items: center;
  justify-content: center;
}

.lesson-icon .completed {
  color: #059669;
}

.lesson-icon .in-progress {
  color: #d97706;
}

.lesson-icon .not-started {
  color: #6b7280;
}

.lesson-info {
  flex: 1;
  min-width: 0;
}

.lesson-title {
  font-size: 0.875rem;
  font-weight: 500;
  color: #374151;
  margin-bottom: 0.25rem;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

.lesson-meta {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-size: 0.75rem;
  color: #6b7280;
}

.lesson-meta svg {
  width: 12px;
  height: 12px;
}

.progress-indicator {
  background: #dcfce7;
  color: #166534;
  padding: 0.125rem 0.375rem;
  border-radius: 4px;
  font-weight: 500;
}

/* Main Content */
.course-main-content {
  flex: 1;
  background: white;
  overflow-y: auto;
}

.lesson-player {
  padding: 2rem;
  max-width: 800px;
  margin: 0 auto;
}

/* No Content States */
.no-lesson-selected,
.no-content-available {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100%;
  text-align: center;
  padding: 3rem 2rem;
  color: #64748b;
}

.no-lesson-selected svg,
.no-content-available svg {
  color: #cbd5e1;
  margin-bottom: 1.5rem;
}

.no-lesson-selected h3,
.no-content-available h3 {
  margin: 0 0 0.5rem 0;
