/* Backdrop */
.tv-modal-backdrop{
  position: fixed; inset: 0;
  background: rgba(0,0,0,.5);
  display: grid; place-items: center;
  z-index: 1000;
}

/* Modal card */
.tv-modal{
  width: min(520px, 92vw);
  background: #fff;
  color: #0b0c2a;
  border-radius: 16px;
  box-shadow: 0 20px 60px rgba(0,0,0,.25);
  padding: 22px 22px 18px;
  position: relative;
  font-family: 'Manrope', sans-serif;
}

/* Close */
.tv-modal-close{
  position: absolute; top: 8px; right: 10px;
  width: 36px; height: 36px;
  border-radius: 50%;
  border: none; background: #f3f3f5;
  font-size: 20px; cursor: pointer;
}

/* Title */
.tv-modal-title{
  margin: 0 0 14px 0;
  font-size: 20px; font-weight: 800;
}

/* Form */
.tv-modal-form{ display: grid; gap: 12px; }
.tv-modal-form label{ display: grid; gap: 6px; font-size: 14px; font-weight: 600; }
.tv-modal-form input,
.tv-modal-form textarea{
  border: 1px solid #e3e6ef; border-radius: 10px;
  padding: 10px 12px; font-size: 14px; outline: none;
}
.tv-modal-form input:focus,
.tv-modal-form textarea:focus{ border-color: #ee6a4d; box-shadow: 0 0 0 3px rgba(238,106,77,.15); }

.tv-modal-submit{
  margin-top: 4px;
  background: #ee6a4d; color: #fff; border: 0;
  padding: 10px 16px; border-radius: 10px; font-weight: 700;
  cursor: pointer;
}

Frontend1/src/pages/Student/Dashboard-pu.content:
/* Purchase History Styles */
.purchases-content {
  padding: 20px;
}

.section-header {
  margin-bottom: 30px;
  text-align: center;
}

.section-header h2 {
  color: #2c3e50;
  font-size: 28px;
  margin-bottom: 10px;
}

.section-header p {
  color: #7f8c8d;
  font-size: 16px;
}

.purchases-section {
  margin-bottom: 40px;
}

.section-title {
  display: flex;
  align-items: center;
  gap: 15px;
  margin-bottom: 20px;
  border-bottom: 2px solid #ecf0f1;
  padding-bottom: 10px;
}

.section-title h3 {
  color: #2c3e50;
  font-size: 22px;
  margin: 0;
}

.loading-indicator {
  color: #3498db;
  font-size: 14px;
  font-style: italic;
}

.empty-state {
  text-align: center;
  padding: 60px 20px;
  background-color: #f8f9fa;
  border-radius: 8px;
  border: 2px dashed #dee2e6;
}

.empty-state svg {
  color: #6c757d;
  margin-bottom: 20px;
}

.empty-state h4 {
  color: #495057;
  margin-bottom: 10px;
}

.empty-state p {
  color: #6c757d;
  margin: 0;
}

.purchases-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(400px, 1fr));
  gap: 20px;
}

.purchase-card {
  background: white;
  border: 1px solid #e1e1e1;
  border-radius: 12px;
  padding: 24px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;
}

.purchase-card:hover {
  transform: translateY(-2px);
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.15);
}

.purchase-header {
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  margin-bottom: 20px;
}

.course-info h4 {
  color: #2c3e50;
  font-size: 18px;
  margin: 0 0 8px 0;
  font-weight: 600;
}

.course-info p {
  color: #7f8c8d;
  font-size: 14px;
  margin: 0;
  line-height: 1.4;
}

.status-badge {
  color: white;
  padding: 6px 12px;
  border-radius: 20px;
  font-size: 12px;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.purchase-details {
  margin-bottom: 20px;
}

.detail-row {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 12px;
  padding: 8px 0;
  border-bottom: 1px solid #f8f9fa;
}

.detail-row:last-child {
  border-bottom: none;
  margin-bottom: 0;
}

.detail-row span:first-child {
  color: #495057;
  font-weight: 500;
}

.detail-row span:last-child {
  color: #2c3e50;
  font-weight: 600;
}

.detail-row .amount {
  color: #27ae60;
  font-size: 16px;
  font-weight: bold;
}

.purchase-actions {
  padding-top: 16px;
  border-top: 1px solid #f8f9fa;
}

.download-btn {
  background-color: #3498db;
  color: white;
  border: none;
  padding: 10px 16px;
  border-radius: 6px;
  cursor: pointer;
  display: inline-flex;
  align-items: center;
  gap: 8px;
  font-size: 14px;
  font-weight: 500;
  transition: all 0.3s ease;
}

.download-btn:hover {
  background-color: #2980b9;
  transform: translateY(-1px);
}

.download-btn.small {
  padding: 6px 10px;
  font-size: 12px;
  margin-right: 5px;
}

.receipts-table {
  background: white;
  border-radius: 8px;
  overflow: hidden;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
}

.receipts-table table {
  width: 100%;
  border-collapse: collapse;
}

.receipts-table th {
  background-color: #f8f9fa;
  color: #495057;
  padding: 16px;
  text-align: left;
  font-weight: 600;
  border-bottom: 2px solid #dee2e6;
}

.receipts-table td {
  padding: 16px;
  border-bottom: 1px solid #f8f9fa;
  color: #495057;
}

.receipts-table tr:hover {
  background-color: #f8f9fa;
}

.receipt-actions {
  display: flex;
  gap: 5px;
}

/* Responsive design */
.offline-upload {
  background: #ffffff;
  border: 1px solid #e1e1e1;
  border-radius: 10px;
  padding: 16px;
  margin: 12px 0 24px;
}
.upload-row {
  display: grid;
  grid-template-columns: 2fr 1fr 2fr 2fr auto;
  gap: 12px;
  align-items: end;
}
.upload-field label {
  display: block;
  font-size: 12px;
  color: #6b7280;
  margin-bottom: 6px;
}
.upload-field input,
.upload-field select {
  width: 100%;
  padding: 10px 12px;
  border: 1px solid #e5e7eb;
  border-radius: 6px;
  font-size: 14px;
}
.upload-actions button[disabled] {
  opacity: 0.6;
  cursor: not-allowed;
}
.link {
  color: #2563eb;
  text-decoration: none;
}
.link:hover {
  text-decoration: underline;
}

@media (max-width: 768px) {
  .purchases-grid {
    grid-template-columns: 1fr;
  }

  .purchase-header {
    flex-direction: column;
    gap: 15px;
  }

  .receipts-table {
    overflow-x: auto;
  }

  .receipts-table table {
    min-width: 600px;
  }
  .upload-row {
    grid-template-columns: 1fr;
  }
}

Frontend1/src/pages/mainAdmin/StudyMater.content:
.study-materials-container {
  padding: 24px;
  max-width: 1400px;
  margin: 0 auto;
  background-color: #f8f9fa;
  min-height: 100vh;
}

/* Header */
.study-materials-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 32px;
  background: white;
  padding: 24px;
  border-radius: 12px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
}

.header-left h1 {
  display: flex;
  align-items: center;
  gap: 12px;
  margin: 0 0 8px 0;
  color: #2c3e50;
  font-size: 28px;
  font-weight: 600;
}

.header-left p {
  margin: 0;
  color: #6c757d;
  font-size: 16px;
}

.upload-btn {
  display: flex;
  align-items: center;
  gap: 8px;
  background: linear-gradient(135deg, #007bff, #0056b3);
  color: white;
  border: none;
  padding: 12px 24px;
  border-radius: 8px;
  font-size: 16px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.3s ease;
}

.upload-btn:hover {
  transform: translateY(-2px);
  box-shadow: 0 4px 12px rgba(0, 123, 255, 0.3);
}

/* Filters */
.materials-filters {
  display: flex;
  gap: 16px;
  margin-bottom: 24px;
  background: white;
  padding: 20px;
  border-radius: 12px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  align-items: center;
  flex-wrap: wrap;
}

.filter-group {
  display: flex;
  align-items: center;
  gap: 8px;
  position: relative;
}

.filter-group select {
  padding: 10px 16px;
  border: 2px solid #e9ecef;
  border-radius: 8px;
  background: white;
  font-size: 14px;
  color: #495057;
  cursor: pointer;
  transition: border-color 0.3s ease;
  min-width: 150px;
}

.filter-group select:focus {
  outline: none;
  border-color: #007bff;
}

.filter-icon {
  color: #6c757d;
  font-size: 16px;
}

.search-group {
  display: flex;
  align-items: center;
  position: relative;
  margin-left: auto;
}

.search-icon {
  position: absolute;
  left: 12px;
  color: #6c757d;
  font-size: 14px;
}

.search-group input {
  padding: 10px 16px 10px 40px;
  border: 2px solid #e9ecef;
  border-radius: 8px;
  background: white;
  font-size: 14px;
  color: #495057;
  transition: border-color 0.3s ease;
  min-width: 250px;
}

.search-group input:focus {
  outline: none;
  border-color: #007bff;
}

/* Table */
.materials-table-container {
  background: white;
  border-radius: 12px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  overflow: hidden;
  margin-bottom: 24px;
}

.materials-table {
  width: 100%;
  border-collapse: collapse;
}

.materials-table th {
  background: #f8f9fa;
  padding: 16px;
  text-align: left;
  font-weight: 600;
  color: #495057;
  border-bottom: 2px solid #e9ecef;
  font-size: 14px;
}

.materials-table td {
  padding: 16px;
  border-bottom: 1px solid #e9ecef;
  vertical-align: middle;
}

.materials-table tr:hover {
  background-color: #f8f9fa;
}

.material-info .material-details {
  display: flex;
  align-items: center;
  gap: 12px;
}

.file-icon {
  font-size: 24px;
  min-width: 32px;
}

.material-details h4 {
  margin: 0 0 4px 0;
  color: #2c3e50;
  font-size: 16px;
  font-weight: 500;
}

.material-details p {
  margin: 0;
  color: #6c757d;
  font-size: 14px;
  max-width: 300px;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

.subject-badge,
.type-badge {
  display: inline-block;
  padding: 4px 12px;
  border-radius: 20px;
  font-size: 12px;
  font-weight: 500;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.subject-badge {
  background: linear-gradient(135deg, #e3f2fd, #bbdefb);
  color: #1976d2;
}

.type-badge {
  background: linear-gradient(135deg, #f3e5f5, #e1bee7);
  color: #7b1fa2;
}

.actions {
  display: flex;
  gap: 8px;
}

.action-btn {
  padding: 8px;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  transition: all 0.3s ease;
  font-size: 14px;
  min-width: 32px;
  height: 32px;
  display: flex;
  align-items: center;
  justify-content: center;
}

.view-btn {
  background: #e3f2fd;
  color: #1976d2;
}

.view-btn:hover {
  background: #bbdefb;
}

.edit-btn {
  background: #fff3e0;
  color: #f57c00;
}

.edit-btn:hover {
  background: #ffe0b2;
}

.delete-btn {
  background: #ffebee;
  color: #d32f2f;
}

.delete-btn:hover {
  background: #ffcdd2;
}

.no-data {
  text-align: center;
  padding: 48px;
  color: #6c757d;
  font-style: italic;
}

/* Loading */
.loading-container {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 48px;
  gap: 16px;
}

.loading-spinner {
  width: 40px;
  height: 40px;
  border: 4px solid #e9ecef;
  border-top: 4px solid #007bff;
  border-radius: 50%;
  animation: spin 1s linear infinite;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

/* Pagination */
.pagination {
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 16px;
  background: white;
  padding: 20px;
  border-radius: 12px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
}

.pagination button {
  padding: 8px 16px;
  border: 2px solid #e9ecef;
  background: white;
  color: #495057;
  border-radius: 6px;
  cursor: pointer;
  transition: all 0.3s ease;
}

.pagination button:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

.pagination button:not(:disabled):hover {
  border-color: #007bff;
  color: #007bff;
}

.page-info {
  color: #6c757d;
  font-weight: 500;
}

/* Modal */
.modal-overlay {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  padding: 20px;
}

.upload-modal {
  background: white;
  border-radius: 16px;
  max-width: 600px;
  width: 100%;
  max-height: 90vh;
  overflow-y: auto;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
}

.modal-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 24px;
  border-bottom: 1px solid #e9ecef;
}

.modal-header h2 {
  margin: 0;
  color: #2c3e50;
  font-size: 24px;
  font-weight: 600;
}

.close-btn {
  background: none;
  border: none;
  font-size: 24px;
  color: #6c757d;
  cursor: pointer;
  padding: 4px;
  line-height: 1;
  transition: color 0.3s ease;
}

.close-btn:hover {
  color: #495057;
}

.upload-form {
  padding: 24px;
}

.form-group {
  margin-bottom: 20px;
}

.form-row {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 16px;
}

.form-group label {
  display: block;
  margin-bottom: 8px;
  color: #495057;
  font-weight: 500;
  font-size: 14px;
}

.form-group input,
.form-group select,
.form-group textarea {
  width: 100%;
  padding: 12px 16px;
  border: 2px solid #e9ecef;
  border-radius: 8px;
  font-size: 14px;
  color: #495057;
  transition: border-color 0.3s ease;
  box-sizing: border-box;
}

.form-group input:focus,
.form-group select:focus,
.form-group textarea:focus {
  outline: none;
  border-color: #007bff;
}

.form-group textarea {
  resize: vertical;
  min-height: 80px;
}

.form-group small {
  display: block;
  margin-top: 4px;
  color: #6c757d;
  font-size: 12px;
}

.form-actions {
  display: flex;
  justify-content: flex-end;
  gap: 12px;
  margin-top: 32px;
  padding-top: 20px;
  border-top: 1px solid #e9ecef;
}

.cancel-btn {
  padding: 12px 24px;
  border: 2px solid #e9ecef;
  background: white;
  color: #495057;
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.3s ease;
  font-size: 14px;
  font-weight: 500;
}

.cancel-btn:hover {
  border-color: #6c757d;
  color: #6c757d;
}

.submit-btn {
  padding: 12px 24px;
  border: none;
  background: linear-gradient(135deg, #28a745, #20c997);
  color: white;
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.3s ease;
  font-size: 14px;
  font-weight: 500;
}

.submit-btn:hover:not(:disabled) {
  transform: translateY(-2px);
  box-shadow: 0 4px 12px rgba(40, 167, 69, 0.3);
}

.submit-btn:disabled {
  opacity: 0.7;
  cursor: not-allowed;
  transform: none;
  box-shadow: none;
}

/* Responsive */
@media (max-width: 768px) {
  .study-materials-container {
    padding: 16px;
  }

  .study-materials-header {
    flex-direction: column;
    gap: 16px;
    text-align: center;
  }

  .materials-filters {
    flex-direction: column;
    align-items: stretch;
  }

  .search-group {
    margin-left: 0;
  }

  .search-group input {
    min-width: 100%;
  }

  .materials-table-container {
    overflow-x: auto;
  }

  .materials-table {
    min-width: 800px;
  }

  .form-row {
    grid-template-columns: 1fr;
  }

  .modal-overlay {
    padding: 10px;
  }

  .upload-modal {
    max-height: 95vh;
  }
}

@media (max-width: 480px) {
  .header-left h1 {
    font-size: 24px;
  }

  .upload-btn {
    width: 100%;
    justify-content: center;
  }

  .materials-filters {
    padding: 16px;
  }

  .upload-form {
    padding: 16px;
  }

  .modal-header {
    padding: 16px;
  }
}

