Frontend1/src/pages/Student/CourseViewer.content:
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FiPlay, FiDownload, FiCheckCircle, FiClock, FiBookOpen, FiArrowLeft, FiChevronDown, FiChevronRight, FiAlertCircle } from 'react-icons/fi';
import axios from '../../../utils/axiosConfig';
import './CourseViewer.css';

const CourseViewer = () => {
  const { courseId } = useParams();
  const navigate = useNavigate();

  // State management
  const [course, setCourse] = useState(null);
  const [courseContent, setCourseContent] = useState([]);
  const [activeLesson, setActiveLesson] = useState(null);
  const [userProgress, setUserProgress] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [expandedSubjects, setExpandedSubjects] = useState({});
  const [expandedChapters, setExpandedChapters] = useState({});

  // Check if user has access to course
  const [hasAccess, setHasAccess] = useState(false);

  useEffect(() => {
    loadCourseData();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [courseId]);

  const loadCourseData = async () => {
    try {
      setLoading(true);

      // Check user enrollment and access
      const accessCheck = await checkCourseAccess();
      if (!accessCheck) {
        setError('You do not have access to this course');
        setLoading(false);
        return;
      }

      // Load course basic info
      await loadCourseInfo();

      // Load course structure (subjects â†’ chapters â†’ topics â†’ tests)
      await loadCourseStructure();

      // Load user progress
      await loadUserProgress();

      // Set initial active lesson (resume or first unlocked)
      await setInitialLesson();

    } catch (err) {
      console.error('Error loading course data:', err);
      setError('Failed to load course content');
    } finally {
      setLoading(false);
    }
  };

  const checkCourseAccess = async () => {
    try {
      const token = localStorage.getItem('authToken');
      const response = await axios.get('/api/user/student/my-courses', {
        headers: { Authorization: `Bearer ${token}` }
      });

      const unlockedCourse = response.data.courses?.find(
        c => c.courseId._id === courseId && c.status === 'unlocked'
      );

      setHasAccess(!!unlockedCourse);
      return !!unlockedCourse;
    } catch (err) {
      console.error('Error checking course access:', err);
      return false;
    }
  };

  const loadCourseInfo = async () => {
    try {
      const response = await axios.get(`/api/courses/student/published-courses/${courseId}`);
      setCourse(response.data.course);
    } catch (err) {
      console.error('Error loading course info:', err);
    }
  };

  const loadCourseStructure = async () => {
    try {
      const token = localStorage.getItem('authToken');

      // Use the optimized student course structure endpoint
      const structureRes = await axios.get(`/api/student/course/${courseId}/structure`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (!structureRes.data.success) {
        throw new Error(structureRes.data.message || 'Failed to load course structure');
      }

      const { structure } = structureRes.data;

      // Transform the structure to match the expected format
      const courseStructure = structure.map(subject => ({
        id: subject._id,
        name: subject.name,
        type: 'subject',
        hasContent: subject.chapters?.length > 0,
        chapters: subject.chapters.map(chapter => ({
          id: chapter._id,
          name: chapter.name,
          type: 'chapter',
          hasContent: chapter.topics?.length > 0,
          topics: chapter.topics.map(topic => ({
            id: topic._id,
            name: topic.name,
            type: 'topic',
            hasContent: topic.tests?.length > 0,
            tests: topic.tests.map(test => ({
              id: test._id,
              name: test.title || test.name,
              type: 'test',
              duration: test.duration,
              totalMarks: test.totalMarks,
              instructions: test.instructions
            })),
            materials: []
          }))
        })),
        materials: []
      }));

      // Load study materials for this course
      try {
        const materialsRes = await axios.get(`/api/study-materials/student?courseId=${courseId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        const materials = materialsRes.data.materials || [];

        // Add materials to the first subject or create a general section
        materials.forEach(material => {
          const materialNode = {
            id: material._id,
            name: material.title,
            type: material.type.toLowerCase(), // PDF, Video, Notes
            fileName: material.fileName,
            fileSize: material.fileSize,
            downloadUrl: `/api/study-materials/download/${material._id}`
          };

          // Add to first subject if available
          if (courseStructure.length > 0) {
            if (!courseStructure[0].materials) {
              courseStructure[0].materials = [];
            }
            courseStructure[0].materials.push(materialNode);
            courseStructure[0].hasContent = true;
          }
        });
      } catch (materialErr) {
        console.log('No study materials found for this course');
      }

      // Auto-expand subjects that have content
      const initialExpandedSubjects = {};
      const initialExpandedChapters = {};

      courseStructure.forEach(subject => {
        if (subject.hasContent) {
          initialExpandedSubjects[subject.id] = true;
          subject.chapters.forEach(chapter => {
            if (chapter.hasContent) {
              initialExpandedChapters[chapter.id] = true;
            }
          });
        }
      });

      setExpandedSubjects(initialExpandedSubjects);
      setExpandedChapters(initialExpandedChapters);
      setCourseContent(courseStructure);

    } catch (err) {
      console.error('Error loading course structure:', err);
      throw err; // Re-throw to be handled by parent function
    }
  };

  const loadUserProgress = async () => {
    try {
      const token = localStorage.getItem('authToken');
      const response = await axios.get(`/api/progress/course/${courseId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (response.data.success) {
        setUserProgress(response.data.progress);
      }
    } catch (err) {
      console.error('Error loading user progress:', err);
      setUserProgress({});
    }
  };

  const setInitialLesson = async () => {
    try {
      // Try to get resume lesson from API
      const token = localStorage.getItem('authToken');
      const resumeResponse = await axios.get(`/api/progress/course/${courseId}/resume`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (resumeResponse.data.success && resumeResponse.data.resumeLesson) {
        const resumeLesson = resumeResponse.data.resumeLesson;
        const lesson = findLessonById(resumeLesson.lessonId, resumeLesson.lessonType);
        if (lesson) {
          setActiveLesson(lesson);
          return;
        }
      }
    } catch (err) {
      console.log('No resume lesson found, starting with first available lesson');
    }

    // Fallback to first available lesson
    if (courseContent.length > 0) {
      const firstLesson = findFirstAvailableLesson(courseContent);
      if (firstLesson) {
        setActiveLesson(firstLesson);
      }
    }
  };

  const findFirstAvailableLesson = (content) => {
    for (const subject of content) {
      for (const chapter of subject.chapters) {
        for (const topic of chapter.topics) {
          if (topic.tests.length > 0) {
            return topic.tests[0];
          }
          if (topic.materials && topic.materials.length > 0) {
            return topic.materials[0];
          }
        }
      }
      if (subject.materials && subject.materials.length > 0) {
        return subject.materials[0];
      }
    }
    return null;
  };

  const findLessonById = (lessonId, lessonType) => {
    for (const subject of courseContent) {
      for (const chapter of subject.chapters) {
        for (const topic of chapter.topics) {
          // Check tests
          const test = topic.tests.find(t => t.id === lessonId && t.type === lessonType);
          if (test) return test;

          // Check materials
          if (topic.materials) {
            const material = topic.materials.find(m => m.id === lessonId && m.type === lessonType);
            if (material) return material;
          }
        }
      }

      // Check subject-level materials
      if (subject.materials) {
        const material = subject.materials.find(m => m.id === lessonId && m.type === lessonType);
        if (material) return material;
      }
    }
    return null;
  };

  const handleLessonSelect = async (lesson) => {
    setActiveLesson(lesson);

    // Track lesson start
    try {
      const token = localStorage.getItem('authToken');
      await axios.post(`/api/progress/course/${courseId}/start-lesson`, {
        lessonId: lesson.id,
        lessonType: lesson.type
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
    } catch (err) {
      console.error('Error tracking lesson start:', err);
    }
  };

  const getLessonProgress = (lessonId) => {
    if (!userProgress.lessonProgress) return { status: 'not_started', progress: 0 };

    const lesson = userProgress.lessonProgress.find(l => l.lessonId === lessonId);
    return lesson || { status: 'not_started', progress: 0 };
  };

  const toggleSubject = (subjectId) => {
    setExpandedSubjects(prev => ({
      ...prev,
      [subjectId]: !prev[subjectId]
    }));
  };

  const toggleChapter = (chapterId) => {
    setExpandedChapters(prev => ({
      ...prev,
      [chapterId]: !prev[chapterId]
    }));
  };

  const getTotalLessons = () => {
    let count = 0;
    courseContent.forEach(subject => {
      if (subject.materials) count += subject.materials.length;
      subject.chapters.forEach(chapter => {
        chapter.topics.forEach(topic => {
          count += topic.tests.length;
          if (topic.materials) count += topic.materials.length;
        });
      });
    });
    return count;
  };

  const renderSidebar = () => {
    const totalLessons = getTotalLessons();

    return (
      <div className="course-sidebar">
        <div className="sidebar-header">
          <button className="back-btn" onClick={() => navigate('/student/dashboard')}>
            <FiArrowLeft /> Back to Dashboard
          </button>
        </div>

        <div className="course-modules">
          {courseContent.length === 0 ? (
            <div className="empty-state">
              <FiAlertCircle size={48} />
              <h3>No Course Structure Found</h3>
              <p>This course doesn't have any subjects or modules yet.</p>
            </div>
          ) : totalLessons === 0 ? (
            <div className="course-structure-info">
              <h4>Course Structure</h4>
              <p className="structure-note">
                ðŸ“š This course has {courseContent.length} subject{courseContent.length !== 1 ? 's' : ''} 
                but no lessons have been added yet.
              </p>

              {courseContent.map((subject) => (
                <div key={subject.id} className="module-section structure-only">
                  <div 
                    className="module-header clickable"
                    onClick={() => toggleSubject(subject.id)}
                  >
                    {expandedSubjects[subject.id] ? <FiChevronDown /> : <FiChevronRight />}
                    <FiBookOpen />
                    <span>{subject.name}</span>
                    <span className="content-count">
                      ({subject.chapters.length} chapter{subject.chapters.length !== 1 ? 's' : ''})
                    </span>
                  </div>

                  {expandedSubjects[subject.id] && (
                    <div className="subject-content">
                      {subject.chapters.length === 0 ? (
                        <div className="empty-chapters">No chapters available</div>
                      ) : (
                        subject.chapters.map((chapter) => (
                          <div key={chapter.id} className="chapter-section structure-only">
                            <div 
                              className="chapter-title clickable"
                              onClick={() => toggleChapter(chapter.id)}
                            >
                              {expandedChapters[chapter.id] ? <FiChevronDown /> : <FiChevronRight />}
                              <span>{chapter.name}</span>
                              <span className="content-count">
                                ({chapter.topics.length} topic{chapter.topics.length !== 1 ? 's' : ''})
                              </span>
                            </div>

                            {expandedChapters[chapter.id] && (
                              <div className="chapter-content">
                                {chapter.topics.length === 0 ? (
                                  <div className="empty-topics">No topics available</div>
                                ) : (
                                  chapter.topics.map((topic) => (
                                    <div key={topic.id} className="topic-section structure-only">
                                      <div className="topic-title">
                                        ðŸ“ {topic.name} ({topic.tests.length} test{topic.tests.length !== 1 ? 's' : ''})
                                      </div>
                                    </div>
                                  ))
                                )}
                              </div>
                            )}
                          </div>
                        ))
                      )}
                    </div>
                  )}
                </div>
              ))}
            </div>
          ) : (
            // Render normal course content with lessons
            courseContent.map((subject) => (
              <div key={subject.id} className="module-section">
                <div 
                  className="module-header clickable"
                  onClick={() => toggleSubject(subject.id)}
                >
                  {expandedSubjects[subject.id] ? <FiChevronDown /> : <FiChevronRight />}
                  <FiBookOpen />
                  <span>{subject.name}</span>
                </div>

                {expandedSubjects[subject.id] && (
                  <div className="subject-content">
                    {subject.chapters.map((chapter) => (
                      <div key={chapter.id} className="chapter-section">
                        <div 
                          className="chapter-title clickable"
                          onClick={() => toggleChapter(chapter.id)}
                        >
                          {expandedChapters[chapter.id] ? <FiChevronDown /> : <FiChevronRight />}
                          <span>{chapter.name}</span>
                        </div>

                        {expandedChapters[chapter.id] && (
                          <div className="chapter-content">
                            {chapter.topics.map((topic) => (
                              <div key={topic.id} className="topic-section">
                                <div className="topic-title">{topic.name}</div>

                                {/* Render tests */}
                                {topic.tests.map((test) => {
                                  const progress = getLessonProgress(test.id);
                                  return (
                                    <div
                                      key={test.id}
                                      className={`lesson-item ${activeLesson?.id === test.id ? 'active' : ''} ${progress.status}`}
                                      onClick={() => handleLessonSelect(test)}
                                    >
                                      <div className="lesson-icon">
                                        {progress.status === 'completed' ? (
                                          <FiCheckCircle className="completed" />
                                        ) : progress.status === 'in_progress' ? (
                                          <FiPlay className="in-progress" />
                                        ) : (
                                          <FiClock className="not-started" />
                                        )}
                                      </div>
                                      <div className="lesson-info">
                                        <div className="lesson-title">{test.name}</div>
                                        <div className="lesson-meta">
                                          <FiClock /> {test.duration} min
                                          {progress.progress > 0 && (
                                            <span className="progress-indicator">
                                              {progress.progress}%
                                            </span>
                                          )}
                                        </div>
                                      </div>
                                    </div>
                                  );
                                })}

                                {/* Render materials */}
                                {topic.materials && topic.materials.map((material) => {
                                  const progress = getLessonProgress(material.id);
                                  return (
                                    <div
                                      key={material.id}
                                      className={`lesson-item ${activeLesson?.id === material.id ? 'active' : ''} ${progress.status}`}
                                      onClick={() => handleLessonSelect(material)}
                                    >
                                      <div className="lesson-icon">
                                        {progress.status === 'completed' ? (
                                          <FiCheckCircle className="completed" />
                                        ) : material.type === 'video' ? (
                                          <FiPlay className={progress.status === 'in_progress' ? 'in-progress' : 'not-started'} />
                                        ) : (
                                          <FiDownload className={progress.status === 'in_progress' ? 'in-progress' : 'not-started'} />
                                        )}
                                      </div>
                                      <div className="lesson-info">
                                        <div className="lesson-title">{material.name}</div>
                                        <div className="lesson-meta">
                                          {material.type.toUpperCase()}
                                          {progress.progress > 0 && (
                                            <span className="progress-indicator">
                                              {progress.progress}%
                                            </span>
                                          )}
                                        </div>
                                      </div>
                                    </div>
                                  );
                                })}
                              </div>
                            ))}
                          </div>
                        )}
                      </div>
                    ))}

                    {/* Subject-level materials */}
                    {subject.materials && subject.materials.map((material) => (
                      <div
                        key={material.id}
                        className={`lesson-item ${activeLesson?.id === material.id ? 'active' : ''}`}
                        onClick={() => handleLessonSelect(material)}
                      >
                        <div className="lesson-icon">
                          {material.type === 'video' ? <FiPlay /> : <FiDownload />}
                        </div>
                        <div className="lesson-info">
                          <div className="lesson-title">{material.name}</div>
                          <div className="lesson-meta">{material.type.toUpperCase()}</div>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            ))
          )}
        </div>
      </div>
    );
  };

  const renderMainContent = () => {
    const totalLessons = getTotalLessons();

    return (
      <div className="course-main-content">
        {activeLesson ? (
          <div className="lesson-player">
            {activeLesson.type === 'test' ? (
              <div className="test-content">
                <div className="test-header">
                  <h2>{activeLesson.name}</h2>
                  <div className="test-meta">
                    <span><FiClock /> {activeLesson.duration} minutes</span>
                    <span>Total Marks: {activeLesson.totalMarks}</span>
                  </div>
                </div>
                <div className="test-instructions">
                  <h3>Instructions:</h3>
                  <p>{activeLesson.instructions || 'No specific instructions provided.'}</p>
                </div>
                <div className="test-actions">
                  <button className="start-test-btn primary">
                    <FiPlay /> Start Test
                  </button>
                </div>
              </div>
            ) : activeLesson.type === 'video' ? (
              <div className="video-content">
                <div className="video-header">
                  <h2>{activeLesson.name}</h2>
                </div>
                <div className="video-placeholder">
                  <FiPlay size={64} />
                  <p>Video player will be implemented here</p>
                  <p>File: {activeLesson.fileName}</p>
                </div>
              </div>
            ) : (
              <div className="document-content">
                <div className="document-header">
                  <h2>{activeLesson.name}</h2>
                  <div className="document-actions">
                    <a 
                      href={activeLesson.downloadUrl} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="download-btn"
                    >
                      <FiDownload /> Download {activeLesson.type.toUpperCase()}
                    </a>
                  </div>
                </div>
                <div className="document-preview">
                  <p>Document preview will be implemented here</p>
                  <p>File: {activeLesson.fileName}</p>
                  <p>Size: {(activeLesson.fileSize / 1024 / 1024).toFixed(2)} MB</p>
                </div>
              </div>
            )}
          </div>
        ) : totalLessons === 0 ? (
          <div className="no-content-available">
            <FiAlertCircle size={64} />
            <h3>No Lessons Available</h3>
            <p>This course structure has been created but no lessons have been added yet.</p>
            <div className="content-info">
              <p>The course currently contains:</p>
              <ul>
                <li>ðŸ“š {courseContent.length} Subject{courseContent.length !== 1 ? 's' : ''}</li>
                <li>ðŸ“˜ {courseContent.reduce((acc, s) => acc + s.chapters.length, 0)} Chapter{courseContent.reduce((acc, s) => acc + s.chapters.length, 0) !== 1 ? 's' : ''}</li>
                <li>ðŸ“ {courseContent.reduce((acc, s) => acc + s.chapters.reduce((acc2, c) => acc2 + c.topics.length, 0), 0)} Topic{courseContent.reduce((acc, s) => acc + s.chapters.reduce((acc2, c) => acc2 + c.topics.length, 0), 0) !== 1 ? 's' : ''}</li>
              </ul>
              <p className="help-text">
                Lessons, tests, and study materials will appear here once they are added by the instructor.
              </p>
            </div>
          </div>
        ) : (
          <div className="no-lesson-selected">
            <FiBookOpen size={64} />
            <h3>Select a lesson to begin</h3>
            <p>Choose from the {totalLessons} available lesson{totalLessons !== 1 ? 's' : ''} on the left to start learning</p>
          </div>
        )}
      </div>
    );
  };

  if (loading) {
    return (
      <div className="course-viewer loading">
        <div className="loading-spinner">Loading course content...</div>
      </div>
    );
  }

  if (error || !hasAccess) {
    return (
      <div className="course-viewer error">
        <div className="error-message">
          <h2>Access Denied</h2>
          <p>{error || 'You do not have access to this course'}</p>
          <button onClick={() => navigate('/student/dashboard')} className="back-btn">
            Return to Dashboard
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="course-viewer">
      {/* Header */}
      <div className="course-header">
        <div className="course-title">
          <h1>{course?.name || 'Course Content'}</h1>
          <div className="course-progress">
            <div className="progress-bar">
              <div
                className="progress-fill"
                style={{ width: `${userProgress.overallProgress || 0}%` }}
              ></div>
            </div>
            <span className="progress-text">
              {userProgress.overallProgress || 0}% Complete
            </span>
          </div>
        </div>
      </div>

      {/* Main layout */}
      <div className="course-layout">
        {renderSidebar()}
        {renderMainContent()}
      </div>
    </div>
  );
};

export default CourseViewer;

backend1/routes/Otp.js.content:
const express = require("express");
const { sendOtpEmailUtil, sendOtpPhoneUtil } = require("../utils/SendOtp");
const User = require("../models/UserSchema");
const OTP = require("../models/OtpSchema");
const { authMiddleware, adminAuth } = require("../middleware/authMiddleware");
const jwt = require("jsonwebtoken");

const mongoose=require("mongoose")


const router = express.Router();





router.post("/update-details", authMiddleware, async (req, res) => {
  console.log("âœ… Token Decoded User:", req.user);

  try {
    const userId = req.user.id; // âœ… Extracted from JWT
    const { name, email, phoneNumber, city, gender, dob, profilePic } = req.body;

    let user = await User.findById(userId);
    if (!user) return res.status(404).json({ message: "User not found" });

    user.name = name;
    user.email = email;
    user.phoneNumber = phoneNumber;
    user.city = city;
    user.gender = gender;
    user.dob = dob;
    user.profilePic = profilePic;
    await user.save();

    res.json({ message: "User details updated successfully", user, redirectTo: "/exam-category" });
  } catch (error) {
    res.status(500).json({ message: "Server error", error: error.message });
  }
});


router.post("/save-category", authMiddleware, async (req, res) => {
  try {
    const userId = req.user.id;
    const { category } = req.body;

    const user = await User.findById(userId);
    if (!user) return res.status(404).json({ message: "âŒ User not found" });

    user.selectedCategory = category;
    await user.save();

    res.status(200).json({
      message: "âœ… Exam category saved successfully",
      redirectTo: `/exam-selection/${category}`
    });
  } catch (error) {
    console.error("âŒ Error saving category:", error);
    res.status(500).json({ message: "Server error" });
  }
});




router.post("/save-exam", authMiddleware, async (req, res) => {
  try {
    const userId = req.user.id; // âœ… Get from token
    const { category, exam } = req.body;

    if (!category || !exam) {
      return res.status(400).json({ message: "âŒ Category and Exam are required" });
    }

    let user = await User.findById(userId);
    if (!user) {
      return res.status(404).json({ message: "âŒ User not found" });
    }

    user.selectedCategory = category;
    user.selectedExam = exam;
    await user.save();

    res.status(200).json({
      message: "âœ… Exam saved successfully",
      redirectTo: "/study-zone"
    });
  } catch (error) {
    console.error("âŒ Error saving exam:", error);
    res.status(500).json({ message: "Server error" });
  }
});




router.post("/send-email", async (req, res) => {
  try {
    const { email } = req.body;

    if (!email) {
      return res.status(400).json({ message: "Email is required" });
    }

    const otpCode = Math.floor(100000 + Math.random() * 900000).toString();

    // âœ… Delete Old OTP Before Creating New One
    await OTP.deleteMany({ email });

    let user = await User.findOne({ email });
    if (!user) {
      user = new User({ email, isEmailVerified: false });
      await user.save();
    }

    await OTP.create({ userId: user._id, otpCode });

    await sendOtpEmailUtil(email, otpCode);

    res.status(200).json({ message: "OTP sent successfully" });
  } catch (error) {
    res.status(500).json({ message: "Server Error", error });
  }
});

// Send OTP to Phone
router.post("/send-phone", async (req, res) => {
  try {
    const { phoneNumber } = req.body;

    if (!phoneNumber) {
      return res.status(400).json({ message: "Phone number is required" });
    }

    // **Check if User Exists**
    let user = await User.findOne({ phoneNumber });
    if (!user) {
      user = await User.create({ phoneNumber, isPhoneVerified: false });
    }

    // **Generate OTP**
    const otpCode = Math.floor(100000 + Math.random() * 900000).toString();

    // **Save OTP in Database**
    await OTP.create({ userId: user._id, otpCode });

    // **Send OTP via Fast2SMS**

    await sendOtpPhoneUtil(phoneNumber, otpCode);

    res.status(200).json({ message: "OTP sent successfully to Phone" });
  } catch (error) {
    res.status(500).json({ message: "Server Error", error });
  }
});



router.post("/verify", async (req, res) => {
  try {
    console.log("ðŸ” Verifying OTP for:", req.body.email); // Debugging
    const { email, otpCode } = req.body;

    if (!otpCode || !email) {
        console.error("âŒ Missing OTP or Email");
      return res.status(400).json({ message: "Email and OTP are required" });
    }

    let user = await User.findOne({ email });

    if (!user) {
        console.error("âŒ User Not Found:", email);
      return res.status(400).json({ message: "User not found" });
    }

    const otpRecord = await OTP.findOne({ userId: user._id }).sort({ createdAt: -1 });

    if (!otpRecord || otpRecord.otpCode !== otpCode) {
        console.error("âŒ Invalid OTP:", otpCode);
      return res.status(400).json({ message: "Invalid or Expired OTP" });
    }
    console.log("âœ… OTP Verified Successfully for:", email);

    user.isEmailVerified = true;
    await user.save({ validateBeforeSave: false });

    user = await User.findById(user._id);

    // âœ… Generate JWT Token
    const token = jwt.sign(
        { id: user._id, role: user.role },
        process.env.JWT_SECRET || "default_secret_key", // âœ… Ensure JWT_SECRET is always available
        { expiresIn: "1h" }
      );

    // âœ… Determine Redirect Path
    let redirectTo = "/study-zone";  
    const adminEmails = ["takoria2204@gmail.com", "superadmin@lms.com"];
    if (adminEmails.includes(user.email)) {
        redirectTo = "/admin-dashboard"; 
    } else if (user.role === "subadmin") {
        redirectTo = "/subadmin-dashboard"; 
    } else if (!user.name || !user.phoneNumber || !user.city || !user.gender || !user.dob || !user.selectedCategory || !user.selectedExam) {
        redirectTo = "/user-details"; 
    } else if (user.selectedCategory && !user.selectedExam) {
        redirectTo = `/exam-selection/${user.selectedCategory}`;
    } else if (!user.selectedCategory) {
        redirectTo = "/exam-category";
    }

    res.status(200).json({
        message: "OTP verified successfully",
        token,  // âœ… Send token in response
        user,
        redirectTo,
    });

  } catch (error) {
    console.error("âŒ Server Error in OTP Verification:", error);
    res.status(500).json({ message: "Server error", error });
  }
});



router.get("/auto-login", authMiddleware, async (req, res) => {
  try {
    const userId = req.user.id; // âœ… token se mila

    const user = await User.findById(userId);
    if (!user) {
      return res.status(404).json({ message: "âŒ User not found!" });
    }

    let redirectTo = "/user-details";
    if (
      user.name && user.phone && user.city &&
      user.gender && user.dob &&
      user.selectedCategory && user.selectedExam
    ) {
      redirectTo = "/study-zone";
    } else if (user.selectedCategory && !user.selectedExam) {
      redirectTo = `/exam-selection/${user.selectedCategory}`;
    } else if (!user.selectedCategory) {
      redirectTo = "/exam-category";
    }

    res.status(200).json({
      exists: true,
      user: {
        id: user._id,
        email: user.email,
        name: user.name,
        phone: user.phone,
        city: user.city,
        gender: user.gender,
        dob: user.dob,
        selectedCategory: user.selectedCategory,
        selectedExam: user.selectedExam
      },
      redirectTo
    });
  } catch (error) {
    console.error("âŒ Server Error in Auto Login:", error);
    res.status(500).json({ message: "Server error", error });
  }
});




router.get("/get-students", adminAuth, async (req, res) => {

    try {
        const students = await User.find({ role: "student" }).select("name email phoneNumber selectedCategory selectedExam createdAt");
        res.status(200).json({ students });
    } catch (error) {
        console.error("âŒ Error fetching students:", error);
        res.status(500).json({ message: "Server error" });
    }
});



// âœ… Update Student API
router.put("/update-student/:id", adminAuth, async (req, res) => {

    try {
        const { id } = req.params;
        const updatedData = req.body;

        // âœ… Check if user exists
        let student = await User.findById(id);
        if (!student) {
            return res.status(404).json({ message: "Student not found!" });
        }

        // âœ… Update Student Data
        student = await User.findByIdAndUpdate(id, updatedData, { new: true });

        res.status(200).json({ message: "âœ… Student updated successfully!", student });
    } catch (error) {
        console.error("âŒ Error updating student:", error);
        res.status(500).json({ message: "Server error", error });
    }
});

// âœ… Delete Student API
router.delete("/delete-student/:id", adminAuth, async (req, res) => {

    try {
        const { id } = req.params;

        // âœ… Check if user exists
        let student = await User.findById(id);
        if (!student) {
            return res.status(404).json({ message: "Student not found!" });
        }

        // âœ… Delete the Student
        await User.findByIdAndDelete(id);

        res.status(200).json({ message: "âœ… Student deleted successfully!" });
    } catch (error) {
        console.error("âŒ Error deleting student:", error);
        res.status(500).json({ message: "Server error", error });
    }
});


router.post("/check-user", async (req, res) => {
    try {
        const { email } = req.body;

        console.log("ðŸ” Checking user existence for:", email);

        if (!email) {
            return res.status(400).json({ message: "Email is required" });
        }

        let user = await User.findOne({ email });

        if (!user) {
            console.log("âŒ User Not Found!");
            return res.status(200).json({ exists: false });
        }

        console.log("âœ… User Found:", user._id);

        let redirectTo = "/user-details";
        if (user.name && user.phoneNumber && user.gender) {
            redirectTo = "/study-zone";  // âœ… Agar user ki details pehle se hain to direct StudyZone bhejo
        }

        res.status(200).json({ exists: true, user, redirectTo });

    } catch (error) {
        console.error("âŒ Server Error in Checking User:", error);
        res.status(500).json({ message: "Server error", error });
    }
});


router.post("/login-phone", async (req, res) => {
    try {
      const { phoneNumber, otpCode } = req.body;

      if (!phoneNumber || !otpCode) {
        return res.status(400).json({ message: "âŒ Phone number and OTP required!" });
      }

      // âœ… 1ï¸âƒ£ Check if User Exists
      let user = await User.findOne({ phoneNumber });

      if (!user || !user.isPhoneVerified) {
        return res.status(404).json({ message: "âŒ User not found or not verified!" });
      }

      // âœ… 2ï¸âƒ£ Check OTP
      const otpRecord = await OTP.findOne({ userId: user._id }).sort({ createdAt: -1 });

      if (!otpRecord || otpRecord.otpCode !== otpCode) {
        return res.status(400).json({ message: "âŒ Invalid OTP!" });
      }

      // âœ… 3ï¸âƒ£ Generate JWT Token
      const token = jwt.sign(
        { id: user._id, role: user.role },
        process.env.JWT_SECRET || "default_secret_key",
        { expiresIn: "1h" }
      );

      res.status(200).json({ message: "âœ… Login successful!", token, user });
    } catch (error) {
      console.error("âŒ Error in login:", error);
      res.status(500).json({ message: "Server error", error });
    }
  });


router.post("/send-otp", async (req, res) => {
    try {
      const { phoneNumber } = req.body;

      if (!phoneNumber || phoneNumber.length !== 10) {
        return res.status(400).json({ message: "âŒ Valid phone number required!" });
      }

      // âœ… 2ï¸âƒ£ Check if User Already Exists
      let user = await User.findOne({ phoneNumber });
      if (!user) {
        user = new User({ phoneNumber, isPhoneVerified: false });
        await user.save();
      }

      // âœ… 3ï¸âƒ£ Generate OTP
      const otpCode = Math.floor(100000 + Math.random() * 900000).toString();

      // âœ… 4ï¸âƒ£ OTP Database Me Store Karo
      await OTP.create({ userId: user._id, otpCode });

      // âœ… 5ï¸âƒ£ OTP SMS Ke Through Bhejo
      await sendOtpPhoneUtil(phoneNumber, otpCode);

      res.status(200).json({ message: "âœ… OTP sent successfully!" }); // âŒ Don't expose ID

    } catch (error) {
      console.error("âŒ Error sending OTP:", error);
      res.status(500).json({ message: "Server error", error });
    }
  });


router.post("/mobileVerify-otp", async (req, res) => {
    try {

      const { phoneNumber, otpCode } = req.body;

      if (!phoneNumber || !otpCode) {
        return res.status(400).json({ message: "âŒ Phone number and OTP required!" });
      }

      // âœ… 1ï¸âƒ£ Check if User Exists
      let user = await User.findOne({ phoneNumber });
      if (!user) {
        return res.status(404).json({ message: "âŒ User not found!" });
      }

      // âœ… 2ï¸âƒ£ Get Latest OTP from Database
      const otpRecord = await OTP.findOne({ userId: user._id }).sort({ createdAt: -1 });

      if (!otpRecord || otpRecord.otpCode !== otpCode) {
        return res.status(400).json({ message: "âŒ Invalid or expired OTP!" });
      }

      // âœ… 3ï¸âƒ£ Mark User as Verified
      user.isPhoneVerified = true;
      await user.save({ validateBeforeSave: false });

      // âœ… 4ï¸âƒ£ Generate JWT Token
      const token = jwt.sign(
        { id: user._id, role: user.role },
        process.env.JWT_SECRET || "default_secret_key",
        { expiresIn: "1h" }
      );

      // âœ… 5ï¸âƒ£ Smart Redirect Logic
      let redirectTo = "/study-zone";  
    const adminPhone = ["7015242845", "7015242846"];
    if (adminPhone.includes(user.phoneNumber)) {
        redirectTo = "/admin-dashboard"; 
    } else if (user.role === "subadmin") {
        redirectTo = "/subadmin-dashboard"; 
    } else if (!user.name || !user.email || !user.city || !user.gender || !user.dob || !user.selectedCategory || !user.selectedExam) {
        redirectTo = "/user-details"; 
    } else if (user.selectedCategory && !user.selectedExam) {
        redirectTo = `/exam-selection/${user.selectedCategory}`;
    } else if (!user.selectedCategory) {
        redirectTo = "/exam-category";
    }

      res.status(200).json({
        message: "âœ… Mobile number verified successfully!",
        token,
        user,
        redirectTo,
      });

    } catch (error) {
      console.error("âŒ Error verifying OTP:", error);
      res.status(500).json({ message: "Server error", error });
    }
  });


  router.get("/verify-token", async (req, res) => {
  try {
    const authHeader = req.headers.authorization;

    // âœ… Token Missing
    if (!authHeader || !authHeader.startsWith("Bearer ")) {
      return res.status(401).json({ message: "âŒ Token missing or invalid!" });
    }

    // âœ… Decode Token
    const token = authHeader.split(" ")[1];
    const decoded = jwt.verify(token, process.env.JWT_SECRET || "default_secret_key");

    // âœ… Find User
    const user = await User.findById(decoded.id)

    if (!user) {
      return res.status(404).json({ message: "âŒ User not found!" });
    }

    // âœ… Return User + Optional redirect suggestion
    let redirectTo = "/user-details";

    if (
      user.name && user.city && user.gender &&
      user.dob && user.selectedCategory && user.selectedExam
    ) {
      redirectTo = "/study-zone";
    } else if (user.selectedCategory && !user.selectedExam) {
      redirectTo = `/exam-selection/${user.selectedCategory}`;
    } else if (!user.selectedCategory) {
      redirectTo = "/exam-category";
    }

    return res.status(200).json({ user, redirectTo });

  } catch (err) {
    console.error("âŒ Token verify error:", err);
    return res.status(401).json({ message: "âŒ Token expired or invalid!" });
  }
});








module.exports = router;

