import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { FaArrowLeft, FaUserEdit } from "react-icons/fa";
import axios from "axios";
import LOGO from "../../../images/tgLOGO.png";
import "./ExamCategory.css";

const ExamCategory = () => {
  const navigate = useNavigate();
  const [selectedCategory, setSelectedCategory] = useState("");
  const [profilePic, setProfilePic] = useState("");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");

  const categories = ["MBA", "After 12", "GMAT", "GovtExams"];

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (storedUser) {
      setName(storedUser.name || "");
      setEmail(storedUser.email || "");
      setProfilePic(storedUser.profilePic || "https://via.placeholder.com/100");
    }
  }, []);

  const handleNext = async () => {
    if (!selectedCategory) {
      alert("Please select a category.");
      return;
    }

    try {
      const token = localStorage.getItem("authToken");

      const response = await axios.post(
        "/api/user/save-category",
        { category: selectedCategory },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      const updatedUser = { ...JSON.parse(localStorage.getItem("user")), selectedCategory };
      localStorage.setItem("user", JSON.stringify(updatedUser));

      navigate(`/exam-selection/${selectedCategory}`);
    } catch (error) {
      console.error("âŒ Error saving category:", error);
      alert("Failed to save category. Try again.");
    }
  };

  return (
    <div className="exam-category-container">
      {/* LEFT SIDE PROFILE */}
      <div className="userdetails-left login-left-panel">
        <div className="login-logo">
          <img src={LOGO} alt="TathaGat Logo" />

          <div className="userdetails-image-wrapper" style={{ marginTop: "40px" }}>
            <img
              src={profilePic || "https://via.placeholder.com/100"}
              alt="Profile"
              className="userdetails-pic"
            />

          </div>

          <p className="login-tagline">
            Access Your Personalized <br />
            <strong>Dashboard</strong> â€“{" "}
            <span>
              Where Preparation
              <br />
              Meets Performance.
            </span>
          </p>
        </div>
      </div>

      {/* RIGHT SIDE DROPDOWN */}
      <div className="exam-category-right">
        <div className="exam-category-box">
          <div className="exam-category-back" onClick={() => navigate(-1)}>
            <FaArrowLeft style={{ marginRight: "8px" }} /> Back
          </div>
          <h2>Select the category of exam</h2>
          <p>What course are you looking for?</p>

          <select
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
          >
            <option value="">Choose</option>
            {categories.map((cat) => (
              <option key={cat} value={cat}>
                {cat}
              </option>
            ))}
          </select>

          <button onClick={handleNext} disabled={!selectedCategory}>
            Next
          </button>
        </div>
      </div>
    </div>
  );
};

export default ExamCategory;

backend1/controllers/ResponseController..content:
const Response = require("../models/test/Response"); // âœ… Correct model
const Question = require("../models/test/Question");

const submitTest = async (req, res) => {
  try {
    const userId = req.user.id;
    const { testId, answers } = req.body;

    if (!testId || !answers || !Array.isArray(answers)) {
      return res.status(400).json({ message: "Test ID and answers are required" });
    }

    let totalScore = 0;
    const finalAnswers = [];

    for (let ans of answers) {
      const question = await Question.findById(ans.questionId);
      if (!question) continue;

      const isCorrect = ans.selectedOptionIndex === question.correctOptionIndex;
      const marksEarned = isCorrect ? question.marks : -question.negativeMarks;

      totalScore += marksEarned;

      finalAnswers.push({
        questionId: question._id,
        selectedOptionIndex: ans.selectedOptionIndex,
        isCorrect,
        marksEarned
      });
    }

    const response = new Response({
      user: userId,
      test: testId,
      answers: finalAnswers,
      totalScore
    });

    await response.save();

    res.status(201).json({
      success: true,
      message: "âœ… Test submitted successfully",
      totalScore,
      answers: finalAnswers
    });
  } catch (error) {
    res.status(500).json({ success: false, message: "âŒ Submission failed", error: error.message });
  }
};

const getUserResponses = async (req, res) => {
  try {
    const responses = await Response.find({ user: req.user.id }).populate("test");
    res.status(200).json({ success: true, responses });
  } catch (error) {
    res.status(500).json({ message: "âŒ Error fetching responses", error: error.message });
  }
};

module.exports = {
  submitTest,
  getUserResponses
};

